import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import Divider from "@material-ui/core/Divider";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";

import { useData, useDispatchUser } from "../../../../lib/userData";

import Text from "../Text";
import { GET_PORTFOLIOS } from "../../../../lib/queries";
import { useQuery, useMutation } from "@apollo/react-hooks";
import moment from "moment";

import Skeleton from "@material-ui/lab/Skeleton";
const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    // maxWidth: "36ch",
    backgroundColor: theme.palette.background.paper,
  },
  inline: {
    display: "inline",
  },
}));

export default function AlignItemsList() {
  const classes = useStyles();
  const donor = useData();
  const { data, loading, error, refetch } = useQuery(GET_PORTFOLIOS, {
    variables: {
      where: { donor: donor ? donor.id : "" },
      sort: "createdAt:desc",
      start: 0,
      limit: null,
    },
    //skip: !project,
  });

  return (
    <List className={classes.root}>
      <ListItem alignItems="flex-start">
        <ListItemAvatar>
          <Avatar
            alt="Remy Sharp"
            src="https://picsum.photos/200
"
          />
        </ListItemAvatar>
        <ListItemText
          primary="Akhuwat Foundation"
          secondary={
            <React.Fragment>
              <Typography
                component="span"
                variant="body2"
                className={classes.inline}
                color="textPrimary"
              >
                Rs. 100
              </Typography>
              {" — monthly"}
            </React.Fragment>
          }
        />
      </ListItem>
      <Divider variant="inset" component="li" />

      {data ? (
        data.portfolios.map((portfolio) => {
          return (
            <>
              <ListItem alignItems="flex-start">
                <ListItemAvatar>
                  <Avatar
                    alt={portfolio.organization.organizationName}
                    src={
                      portfolio.organization.profilePicture
                        ? `${process.env.PLAIN_URL}${portfolio.organization.profilePicture.url}`
                        : null
                    }
                  />
                </ListItemAvatar>
                <ListItemText
                  primary={portfolio.organization.organizationName}
                  secondary={
                    <React.Fragment>
                      <Typography
                        component="span"
                        variant="body2"
                        className={classes.inline}
                        color="textPrimary"
                      >
                        {`Rs. ${portfolio.paymentSize}`}
                      </Typography>
                      {` — ${portfolio.period}`}
                    </React.Fragment>
                  }
                />
              </ListItem>
              <Divider variant="inset" component="li" />
            </>
          );
        })
      ) : (
        <>
          <ListItem alignItems="flex-start">
            <ListItemAvatar>
              <Skeleton
                animation="wave"
                variant="circle"
                width={40}
                height={40}
              />
            </ListItemAvatar>
            <ListItemText
              disableTypography
              primary={<Skeleton animation="wave" height={10} width="80%" />}
              secondary={<Skeleton animation="wave" height={10} width="60%" />}
            />
          </ListItem>
          <ListItem alignItems="flex-start">
            <ListItemAvatar>
              <Skeleton
                animation="wave"
                variant="circle"
                width={40}
                height={40}
              />
            </ListItemAvatar>
            <ListItemText
              disableTypography
              primary={<Skeleton animation="wave" height={10} width="80%" />}
              secondary={<Skeleton animation="wave" height={10} width="60%" />}
            />
          </ListItem>
          <ListItem alignItems="flex-start">
            <ListItemAvatar>
              <Skeleton
                animation="wave"
                variant="circle"
                width={40}
                height={40}
              />
            </ListItemAvatar>
            <ListItemText
              disableTypography
              primary={<Skeleton animation="wave" height={10} width="80%" />}
              secondary={<Skeleton animation="wave" height={10} width="60%" />}
            />
          </ListItem>
        </>
      )}
    </List>
  );
}
