import React, { Fragment, useEffect } from "react";
import { useState } from "react";
import Image from "../common/src/components/Image";
import Head from "next/head";
import Sticky from "react-stickynode";
import { ThemeProvider } from "styled-components";
import { charityTheme } from "../common/src/theme/charity";
import { ResetCSS } from "../common/src/assets/css/style";
import { DrawerProvider } from "../common/src/contexts/DrawerContext";
import Navbar from "../containers/Charity/Navbar";
import DrawerSection from "../containers/Charity/DrawerSection";
import Footer from "../containers/Charity/Footer";
import {
  GlobalStyle,
  CharityWrapper,
  ContentWrapper,
} from "../containers/Charity/charity.style";
import {
  Box,
  Container,
  Typography,
  Grid,
  Paper,
  Divider,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import Button from "../common/src/components/Button";
import Heading from "../common/src/components/Heading";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import BusinessCenterIcon from "@material-ui/icons/BusinessCenter";
import AssessmentIcon from "@material-ui/icons/Assessment";
import PowerSettingsNewIcon from "@material-ui/icons/PowerSettingsNew";
import TextField from "@material-ui/core/TextField";
import PasswordField from "material-ui-password-field";

import { DropzoneArea } from "material-ui-dropzone";
import axios from "axios";
import cookie from "js-cookie";
import { useRouter } from "next/router";
import { useData, useDispatchUser } from "../lib/userData";
import { useFormik } from "formik";
import * as Yup from "yup";
import { useQuery, useMutation } from "@apollo/react-hooks";
import { UPDATE_ORGANIZATION } from "../lib/mutations";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
}));

function ListItemLink(props) {
  return <ListItem button component="a" {...props} />;
}

export default () => {
  const classes = useStyles();
  const dispatch = useDispatchUser();
  const organization = useData(); //returns context
  const [filedIds, setFileIDs] = useState([]);
  const [logo, setLogo] = useState(null);
  const [cover, setCover] = useState(null);
  const [regFile, setRegFile] = useState(null);
  const [taxFile, setTaxFile] = useState(null);

  const [formValues, setFormValues] = useState("");
  const [updateLoading, setUpdateLoading] = useState(false);
  const [updateError, setUpdateError] = useState("");
  const [updateOrganization] = useMutation(UPDATE_ORGANIZATION, {
    onCompleted: (data) => {
      console.log(data);
      dispatch({
        type: "UPDATE",
        payload: data.updateOrganization.donor,
      });
      setUpdateError("Changes saved succesfully!");
      setUpdateLoading(false);
    },
    onError: (error) => {
      console.log(error);
      setUpdateError("Sorry an error occurred. Please try again!");
    },
  });
  const schemas = [
    {
      email: Yup.string()
        .email("Email not valid")
        .required("Please Enter Your Email"),

      focalPerson: Yup.string().required("Please Enter Full Name"),
      mobileNumber: Yup.string().required("Please Enter Your Mobile Number"),
      cnic: Yup.string().required("Please Enter Your CNIC Number"),
    },
  ];
  const {
    handleBlur,
    handleChange,
    handleSubmit,
    handleReset,
    values,
    touched,
    errors,
    setFieldValue,
  } = useFormik({
    initialValues: {
      focalPerson: "",
      email: "",
      mobileNumber: "",
      cnic: "",
    },
    onSubmit: (values) => {
      console.log(values);
      setUpdateLoading(true);
      setUpdateError(null);
      setFormValues(values);
      updateOrganization({
        variables: {
          fields: {
            data: {
              focalPerson: values.focalPerson,
              mobileNumber: values.mobileNumber,
              address: values.address,
              organizationName: values.organizationName,
              registrationNumber: values.registrationNumber,
            },
            where: { id: organization.id },
          },
        },
      });
    },
    validationSchema: Yup.object().shape(schemas[0]),
  });
  useEffect(() => {
    console.log(organization, "organization");
    if (organization["id"]) {
      setFieldValue("organizationName", organization.organizationName);
      setFieldValue("registrationNumber", organization.registrationNumber);
      setFieldValue("focalPerson", organization.focalPerson);
      setFieldValue("email", organization.user.email);
      setFieldValue("address", organization.address);
      setFieldValue("mobileNumber", organization.mobileNumber);
    }
  }, [organization]);

  const handlefiles = async (files, type) => {
    console.log(files, type);
    if (files.length) {
      setUpdateLoading(true);

      const form = new FormData();
      files.forEach((file) => {
        form.append("files", file);
      });

      await axios
        .post("http://localhost:1337/upload", form, {
          headers: {
            "Content-type": "multipart/form-data",
          },
        })
        .then((res) => {
          console.log(res.data);
          //setFileIDs([...filedIds, ...res.data]);
          if (type == "logo") {
            setImgFile(res.data[0]);
          }
          if (type == "cover") {
            setTaxFile(res.data[0]);
          }
          setUpdateLoading(false);
        });
    }
  };
  return (
    <ThemeProvider theme={charityTheme}>
      <Fragment>
        {/* Start charity head section */}
        <Head>
          <title>My Account | Esaar</title>
          <meta name="Description" content="React next landing page" />
          <meta name="theme-color" content="#FCF22B" />
          <meta
            name="viewport"
            content="width=device-width, initial-scale=1.0"
          />
          {/* Load google fonts */}
          <link
            href="https://fonts.googleapis.com/css?family=Open+Sans:300,400,600,700,800&display=swap"
            rel="stylesheet"
          />
        </Head>
        <ResetCSS />
        <GlobalStyle />
        <CharityWrapper>
          <Sticky top={0} innerZ={9999} activeClass="sticky-nav-active">
            <Navbar />
          </Sticky>
          <DrawerProvider>
            <DrawerSection />
          </DrawerProvider>
          <ContentWrapper>
            <Grid
              container
              style={{
                maxWidth: "1170px",
                minHeight: "100vh",
                paddingTop: "140px",
                marginLeft: "170px",
              }}
            >
              <Grid item md={3}>
                <Paper style={{ height: "90vh" }}>
                  <Container style={{ padding: "40px" }}>
                    <div className={classes.root}>
                      <List component="nav" aria-label="main mailbox folders">
                        <ListItem button component="a" href="/account_o">
                          <ListItemIcon>
                            <AccountCircleIcon />
                          </ListItemIcon>
                          <ListItemText primary="Account" />
                        </ListItem>
                        <Divider />
                        <ListItem button component="a" href="/addProject">
                          <ListItemIcon>
                            <BusinessCenterIcon />
                          </ListItemIcon>
                          <ListItemText primary="Add Project " />
                        </ListItem>
                        <Divider />
                        <ListItem button component="a" href="/myProjects">
                          <ListItemIcon>
                            <AssessmentIcon />
                          </ListItemIcon>
                          <ListItemText primary="Projects" />
                        </ListItem>
                        <Divider />
                        <ListItem button>
                          <ListItemIcon>
                            <PowerSettingsNewIcon />
                          </ListItemIcon>
                          <ListItemText primary="Logout" />
                        </ListItem>
                      </List>
                    </div>
                  </Container>
                </Paper>
              </Grid>
              <Grid item md={8} style={{ marginLeft: "30px" }}>
                <Paper style={{ height: "100%" }}>
                  <Container style={{ padding: "40px" }}>
                    {" "}
                    <Heading content="Profile" color="#05B890" /> <Divider />
                    <Grid container>
                      <Grid item md={5} style={{ marginTop: "30px" }}>
                        <Heading
                          content="Organization Name"
                          as="h4"
                          style={{ marginTop: "1rem" }}
                        />{" "}
                        <TextField
                          value={values.organizationName}
                          id="outlined-basic"
                          label=""
                          variant="outlined"
                          size="small"
                          style={{ color: "#3E2672" }}
                          fullWidth
                        />
                        <Heading
                          content="Focal Person"
                          as="h4"
                          style={{ marginTop: "1rem" }}
                        />{" "}
                        {/* FIXME: Color of text fields */}
                        <TextField
                          name="focalPerson"
                          value={values.focalPerson}
                          onChange={handleChange}
                          onBlur={handleBlur}
                          error={errors.focalPerson && touched.focalPerson}
                          helperText={
                            errors.focalPerson && touched.focalPerson
                              ? errors.focalPerson
                              : null
                          }
                          id="outlined-basic"
                          label=""
                          variant="outlined"
                          size="small"
                          fullWidth
                        />
                        <Heading
                          content="Email ID"
                          as="h4"
                          style={{ marginTop: "1rem" }}
                        />{" "}
                        {/* FIXME: Color of text fields */}
                        <TextField
                          type="email"
                          name="email"
                          inputProps={{
                            readOnly: true,
                          }}
                          value={values.email}
                          onChange={handleChange}
                          onBlur={handleBlur}
                          error={errors.email && touched.email}
                          helperText={
                            errors.email && touched.email ? errors.email : null
                          }
                          label=""
                          variant="outlined"
                          size="small"
                          style={{ color: "#05B890" }}
                          fullWidth
                        />
                        <Heading
                          content="Logo"
                          as="h4"
                          style={{ marginTop: "1rem" }}
                        />
                        <DropzoneArea
                          acceptedFiles={["image/*"]}
                          dropzoneText={"Drag and drop images here or click"}
                          onChange={(files) => handlefiles(files, "logo")}
                        />
                      </Grid>
                      <Grid item md={1} />
                      <Grid item md={5} style={{ marginTop: "30px" }}>
                        <Heading
                          content="Registration Number"
                          as="h4"
                          style={{ marginTop: "1rem" }}
                        />{" "}
                        {/* FIXME: Color of text fields */}
                        <TextField
                          name="registrationNumber"
                          value={values.registrationNumber}
                          id="outlined-basic"
                          label=""
                          variant="outlined"
                          size="small"
                          style={{ color: "#3E2672" }}
                          fullWidth
                        />
                        <Heading
                          content="Contact Number"
                          as="h4"
                          style={{ marginTop: "1rem" }}
                        />{" "}
                        {/* FIXME: Color of text fields */}
                        <TextField
                          name="mobileNumber"
                          value={values.mobileNumber}
                          onChange={handleChange}
                          onBlur={handleBlur}
                          error={errors.mobileNumber && touched.mobileNumber}
                          helperText={
                            errors.mobileNumber && touched.mobileNumber
                              ? errors.mobileNumber
                              : null
                          }
                          id="outlined-basic"
                          label=""
                          variant="outlined"
                          size="small"
                          fullWidth
                        />
                        <Heading
                          content="Address"
                          as="h4"
                          style={{ marginTop: "1rem" }}
                        />{" "}
                        {/* FIXME: Color of text fields */}
                        <TextField
                          name="address"
                          value={values.address}
                          id="outlined-basic"
                          label=""
                          variant="outlined"
                          multiline
                          rows={1}
                          size="small"
                          style={{ color: "#05B890" }}
                          fullWidth
                        />
                        <Heading
                          content="Cover"
                          as="h4"
                          style={{ marginTop: "1rem" }}
                        />
                        <DropzoneArea
                          acceptedFiles={["image/*"]}
                          dropzoneText={"Drag and drop images here or click"}
                          onChange={(files) => handlefiles(files, "cover")}
                        />
                      </Grid>
                      <Grid
                        container
                        item
                        md={12}
                        style={{ alignContent: "center" }}
                      >
                        <a href="/project">
                          <Button
                            title="Save Changes"
                            variant="textButton"
                            style={{
                              marginTop: "20px",
                              marginLeft: "230px",

                              display: "flex",
                              alignItems: "center",
                              justifyContent: "center",
                              minWidth: "200px",
                              height: "auto",
                              border: "0",
                              fontSize: "15px",
                              fontWeight: "700",
                              borderRadius: "10px",
                              cursor: "pointer",
                              color: "#FFFFFF",
                              backgroundColor: "#05B890",
                              position: "relative",
                              overflow: "hidden",
                              zIndex: "1",
                            }}
                          />
                        </a>
                      </Grid>
                    </Grid>
                    <Grid item md={12} style={{ marginTop: "30px" }}>
                      <Heading content="Documents" color="#05B890" />
                      <Divider />
                      <Heading
                        content="Registration Certificate"
                        as="h4"
                        style={{ marginTop: "1rem" }}
                      />
                      <DropzoneArea
                        acceptedFiles={["image/*"]}
                        dropzoneText={"Drag and drop images here or click"}
                        onChange={(files) => console.log("Files:", files)}
                      />
                      <Heading
                        content="Tax Exemption Form"
                        as="h4"
                        style={{ marginTop: "1rem" }}
                      />
                      <DropzoneArea
                        acceptedFiles={["image/*"]}
                        dropzoneText={"Drag and drop images here or click"}
                        onChange={(files) => console.log("Files:", files)}
                      />
                      <Grid
                        container
                        item
                        md={12}
                        style={{ alignContent: "center" }}
                      >
                        <a href="/project">
                          <Button
                            title="Save Changes"
                            variant="textButton"
                            style={{
                              marginTop: "20px",
                              marginLeft: "250px",

                              display: "flex",
                              alignItems: "center",
                              justifyContent: "center",
                              minWidth: "200px",
                              height: "auto",
                              border: "0",
                              fontSize: "15px",
                              fontWeight: "700",
                              borderRadius: "10px",
                              cursor: "pointer",
                              color: "#FFFFFF",
                              backgroundColor: "#05B890",
                              position: "relative",
                              overflow: "hidden",
                              zIndex: "1",
                            }}
                          />
                        </a>
                      </Grid>
                    </Grid>
                  </Container>
                </Paper>
              </Grid>
            </Grid>
          </ContentWrapper>
          <Footer />
        </CharityWrapper>
        {/* End of charity wrapper section */}
      </Fragment>
    </ThemeProvider>
  );
};
