  useEffect(() => {
    console.log(data);
  }, [data]);
  if (error) {
    return <Error statusCode={404} />;
  }

  if (!error && !loading && !data) {
    return <Error statusCode={404} />;
  }
  if (loading && !data) {
    return (
      <Container
        maxWidth="xl"
        style={{
          minHeight: "100vh",
          background: "#F2F2F2",

          textAlign: "center",
          paddingTop: "30%",
        }}
      >
        <CircularProgress color="secondary" />
      </Container>
    );
  }

  // if (data && !loading) {
  return (
    <ThemeProvider theme={charityTheme}>
