import React from "react";
import { makeStyles } from "@material-ui/core/styles";

import Heading from "../../../common/src/components/Heading";
import { useQuery, useMutation } from "@apollo/react-hooks";
import Chip from "@material-ui/core/Chip";
import { GET_TAGS } from "../../../lib/queries";
import { Divider } from "@material-ui/core";
//import DoneIcon from '@material-ui/icons/Done';

const useStyles = makeStyles((theme) => ({}));
{
  !values.additionalFeatures.length && (
    <Typography variant="caption"> No Additional Features Selected </Typography>
  );
}
{
  values.additionalFeatures.map((feature) => {
    return (
      <Chip
        key={feature.label}
        size="small"
        color="secondary"
        label={feature.label}
        style={{ margin: "3px" }}
        onDelete={() => handleFeaDelete(feature)}
      />
    );
  });
}

export default function TagSelection() {
  const classes = useStyles();
  const [addTags, setAddTags] = useState([]);

  const handleAddTag = (tag) => {
    console.log(tag);
    setAddTags((item) => item.filter((fet) => fet.key != tag.key));
    // setFieldValue("additionalFeatures", values.additionalFeatures.concat(data));
  };
  const handleDeleteTag = (tag) => {
    console.log(tag);
    // setFieldValue(
    //   "additionalFeatures",
    //   values.additionalFeatures.filter((fet) => fet.key != data.key)
    // );

    setAddTags((addTags) => addTags.concat(tag));
  };

  const { data, loading, error } = useQuery(GET_TAGS);

  return (
    <>
      <Heading content="Tags" as="h4" style={{ marginTop: "1rem" }} />{" "}
      <Chip
        label="Clickable"
        //onClick={handleClick}
        variant="outlined"
      />
      <Divider style={{ marginTop: "10px", marginBottom: "10px" }} />
      <Chip
        label="Clickable"
        //onClick={handleClick}
        variant="outlined"
      />
    </>
  );
}
