import React from "react";
import Link from "next/link";
import Fade from "react-reveal/Fade";
import Text from "../../../common/src/components/Text";
import Heading from "../../../common/src/components/Heading";
import Image from "../../../common/src/components/Image";
import Tab, { Panel } from "../../../common/src/components/Tabs";
import SectionWrapper, { ContentWrapper } from "./branchSection.style";

import { branchData } from "../../../common/src/data/Charity";

import { useData, useDispatchUser } from "../../../lib/userData";
import { GET_ORGANIZATIONS } from "../../../lib/queries";
import { useQuery, useMutation } from "@apollo/react-hooks";

const BranchSection = () => {
  const { data, loading, error, refetch } = useQuery(GET_ORGANIZATIONS, {
    variables: {
      where: {},
      sort: "createdAt:desc",
      limit: null,
      start: 0,
    },
  });

  const title = (text) => {
    return { __html: text };
  };

  return (
    <SectionWrapper id="branch">
      <Tab active={2}>
        {data ? (
          _.sortBy(data.organizations, [
            function(o) {
              return o.projects.length;
            },
          ]).map((item, index) => {
            if (index <= 6) {
              return (
                <Panel
                  title={<Text content={item.organizationName} />}
                  key={`tab_key${item.id}`}
                >
                  <ContentWrapper>
                    <Fade>
                      <div className="image">
                        <Image
                          src={`${process.env.PLAIN_URL}${item.coverImage.url}`}
                          alt={`${item.organizationName} cover image`}
                        />
                      </div>
                    </Fade>
                    <div className="content">
                      <Heading as="h4" content="TOP ORGANIZATIONS" />
                      {/* <h2 dangerouslySetInnerHTML={title(item.title)} /> */}
                      <h2>{item.mission}</h2>
                      <Text content={item.description} />
                      <Link href={item.linkUrl}>
                        <a className="learn__more-btn">
                          <span className="hyphen" />
                          <span className="btn_text">{item.linkText}</span>
                        </a>
                      </Link>
                    </div>
                  </ContentWrapper>
                </Panel>
              );
            }
          })
        ) : (
          <Panel
            title={<Text content={branchData[0].menuItem} />}
            key={`tab_key${branchData[0].id}`}
          >
            <ContentWrapper>
              <Fade>
                <div className="image">
                  <Image src={branchData[0].image} alt="Charity Landing" />
                </div>
              </Fade>
              <div className="content">
                <Heading as="h4" content={branchData[0].slogan} />
                <h2 dangerouslySetInnerHTML={title(branchData[0].title)} />
                <Text content={branchData[0].description} />
                <Link href={branchData[0].linkUrl}>
                  <a className="learn__more-btn">
                    <span className="hyphen" />
                    <span className="btn_text">{branchData[0].linkText}</span>
                  </a>
                </Link>
              </div>
            </ContentWrapper>
          </Panel>
        )}
      </Tab>
    </SectionWrapper>
  );
};

export default BranchSection;
