import React from "react";
import Link from "next/link";
import PropTypes from "prop-types";
import Box from "../../../common/src/components/Box";
import Image from "../../../common/src/components/Image";
import Text from "../../../common/src/components/Text";
import Heading from "../../../common/src/components/Heading";
import Container from "../../../common/src/components/UI/Container";
import BlockWrapper, {
  ContentWrapper,
  List,
  Item,
  ImageWrapper,
} from "./promotionBlock.style";

import { promotionData } from "../../../common/src/data/Charity";

const PromotionBlock = ({ row, col }) => {
  const { slogan, title, text1, text2, lists, image } = promotionData;
  return (
    <BlockWrapper id="ourCommunity">
      <Container width="1260px">
        <Box className="row" {...row}>
          <Box className="col" {...col}>
            <ContentWrapper>
              {/* <Heading as="h5" content={slogan} />
              <Heading content={title} />
              <Text content={text1} />
              <Text content={text2} />
               */}

              <Heading as="h5" content="CREATE YOUR DONATION PROTFOLIOS" />
              <Heading content="Donate to your favorite organizations every month, hasstle-free!" />
              <Text content="We allow you to choose and donate to the organizations with which you feel the most connected." />
              <Text content="We encourage you to support different organizations. Just by selecting the preferred organizations and the causes, you can donate as low as Rs.10 that gets deducted from your wallet every month. " />
              {/* <List>
                {lists.map((item) => (
                  <Item key={`list_key${item.id}`}>{item.text}</Item>
                ))}
              </List> */}
              <Link href="#1">
                <a className="learn__more-btn">
                  <span className="hyphen" />
                  <span className="btn_text">Learn More </span>
                </a>
              </Link>
            </ContentWrapper>
          </Box>
          <Box className="col" {...col}>
            <ImageWrapper>
              <Image src={image} alt="Charity Landing" />
            </ImageWrapper>
          </Box>
        </Box>
      </Container>
    </BlockWrapper>
  );
};

// PromotionBlock style props
PromotionBlock.propTypes = {
  row: PropTypes.object,
  col: PropTypes.object,
};

// PromotionBlock default style
PromotionBlock.defaultProps = {
  // PromotionBlock row default style
  row: {
    flexBox: true,
    flexWrap: "wrap",
    ml: "-15px",
    mr: "-15px",
  },
  // PromotionBlock col default style
  col: {
    width: ["100%", "50%", "50%"],
    pl: "15px",
    pr: "15px",
    mb: "30px",
  },
};

export default PromotionBlock;
